--CREATE TABLE Movie(id INT PRIMARY KEY, title VARCHAR(100), year INT, rating VARCHAR(10), company VARCHAR(50)) ENGINE = INNODB;
CREATE TABLE Actor(id INT PRIMARY KEY, last VARCHAR(20), first VARCHAR(20), sex VARCHAR(6), dob DATE, dod DATE) ENGINE = INNODB;
CREATE TABLE Director(id INT PRIMARY KEY, last VARCHAR(20), first VARCHAR(20), dob DATE, dod DATE) ENGINE = INNODB;
CREATE TABLE MovieGenre(mid INT PRIMARY KEY, FOREIGN KEY(mid) REFERENCES Movie(id), genre VARCHAR(20)) ENGINE = INNODB;
CREATE TABLE MovieDirector(mid INT PRIMARY KEY, did INT) ENGINE = INNODB;
CREATE TABLE MovieActor(mid INT PRIMARY KEY, aid INT, role VARCHAR(50)) ENGINE = INNODB; 
CREATE TABLE Review(name VARCHAR(20), time TIMESTAMP, mid INT, FOREIGN KEY(mid) REFERENCES Movie(id), rating INT, comment VARCHAR(500)) ENGINE = INNODB;
CREATE TABLE MaxPersonID(id INT PRIMARY KEY) ENGINE = INNODB;
CREATE TABLE MaxMovieID(id INT PRIMARY KEY) ENGINE = INNODB; 